import React, { useContext, useEffect, Fragment } from "react";
import authContext from "../../Context/auth/authContext";
import styled from "@emotion/styled";
import { Link } from "react-router-dom";

const Cabecera = styled.header`
  width: 100%;
  height: 15vh;
  background-color: #303e9f;
  box-shadow: 0px 2px 3px #00196e;
`;
const Wrap = styled.div`
  color: white;
  width: 85%;
  margin: 0px auto;
  height: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
`;

const RightContainer = styled.div`
  display: flex;
  justify-content: space-between;
  width: 50%;
  align-items: center;
  height: 100%;
  flex-wrap: wrap;
  flex-grow: 1;
`;

const Titulo = styled.h1`
  width: 40%;
`;
const Boton = styled.button`
  width: 20%;
  padding: 8px;
  border: none;
  background-color: #43168b;
  color: white;
  transition: all 500ms;
  &:hover {
    background-color: #110060;
  }
`;
const Enlace = styled(Link)`
  text-decoration: none;
  color:white;
  transition:all 500ms;
  &:hover{
      color:#7740b8;
  }
`;

const Header = (props) => {
  console.log("props", props);
  //extraer informacion de usuario
  const { usuario, usuarioAutenticado, cerrarSesion } = useContext(authContext);

  useEffect(() => {
    usuarioAutenticado();
  }, []);

  return (
    <Fragment>
      <Cabecera>
        <Wrap>
          <Titulo>Header</Titulo>
          <RightContainer>
            {usuario ? (
              <p>
                hola <span> {usuario.nombre}</span>
              </p>
            ) : null}
            <Enlace to="/Estadisticas">Estadisticas</Enlace>
            <Enlace to="/Perfil">Perfil</Enlace>
            <Enlace to="/Jugar">Jugar</Enlace>

            <Boton
              onClick={() => {
                cerrarSesion();
              }}
            >
              Cerrar Sesion
            </Boton>
          </RightContainer>
        </Wrap>
      </Cabecera>
    </Fragment>
  );
};

export default Header;
